INSERT INTO `comment` VALUES (1867, 1, '100026694212', '官方原装壳，做工精良，用料考究。不同于第三方的硅胶很快就磨光打油，这个壳在较高强度下使用表现出不错的耐用性，对手机的适配性没的说，手感非常好，防滑效果出众。一句话，除了贵啥都好', 'storage.360buyimg.com/i.imageUpload/61737361766c74657231343331303739363134373038_sma.jpg', 'Apple iPhone 13 Pro Max 专用 MagSafe 透明保护壳 iPhone保护套 手机壳', 5, 'a***r', '0', '2022-01-15 22:05:54');
